mapper.update:
UPDATE KT_USER
SET LAST_LOGIN = #{loginDate}, LOGIN_IP = #{loginIP}, LOGIN_STATUS = 1
WHERE USER_ID = #{userId}

UPDATE KT_USER
SET LOGIN_STATUS = 0
WHERE USER_ID = #{userId}

UPDATE KT_USER
SET USER_PASS = #{user_pass}, USER_NAME = #{user_name}, ENABLED = #{enabled}
where USER_ID = #{user_id}

UPDATE KT_USER
SET IDX_URL = #{idx_url}
WHERE USER_ID = #{user_id}
